mapper.select:
SELECT employee_id, last_name, email, phone_number, hire_date, job_id, salary, commission_pct, manager_id, department_id FROM employees
OR department_id=#{departmentId}
OR job_id=#{jobId}
ORDER BY employee_id

mapper.insert:

		SELECT NVL(MAX(employee_id),0) +1
		FROM   employees

	INSERT INTO employees
	        (  
	          employee_id
		      , last_name
		      , email
		      , hire_date
	  	      , job_id

	  	      , salary


			)
    VALUES (
             #{employeeId}
             , #{lastName}
             , #{email}


	             , #{hireDate}             	


             	 , SYSDATE


             , #{jobId}

             , #{salary}

   			)

mapper.update:
	UPDATE employees
	SET 
	      last_name = #{emp.lastName}		      
  	      , job_id = #{emp.jobId}
  	      , salary = #{emp.salary}
    WHERE  employee_id = #{id}
